import {Link} from "react-router-dom"
import '../Modal.css'
import {Modal} from '../Modal.component'


interface Props{}

export const Aula1: React.FC<Props> = () => {
    const [isModalOpen, setModalState] = React.useState(false);

    const toggleModal = () => setModalState(!isModalOpen);

    return (
        <div className={'app'}>
            <div className={'title'}>AULA1</div>
            <p>Per gestire una postazione premi sulla stessa</p>
            <label>Country</label>
            <select id='country' value={country} onChange={onChange}>
                <option value='AX'>Aaland Islands</option>
                <option value='AF'>Afghanistan</option>
            </select>
            <Link to="/">
                <button onClick={() => {}} className={'app__btn'}>go to home</button>
            </Link>

            <button className={'app__btn'} onClick={toggleModal}>
                P1
            </button>

            <Modal 
                title={'My Modal'}
                isOpen={isModalOpen}
                onClose={toggleModal}
            >
                This is some content
            </Modal>

        </div>
    )
}
