import React from 'react';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            flexGrow: 1,
            display: 'flex',
            flexDirection: 'column',
            //alignItems: 'center',
            '& > *': {
                margin: theme.spacing(1),
            }
        },
        paper: {
            padding: theme.spacing(3),
            textAlign: 'center',
            color: theme.palette.text.secondary,
        },
    }),
);


export default function SimpleModal() {
    const classes = useStyles();
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const body = (
        <div style={modalStyle} className={classes.paper}>
            <h2 id="simple-modal-title">Text in a modal</h2>
            <p id="simple-modal-description">
                Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
            <SimpleModal />
        </div>
    );

    //numero di colonne
    function FormRow() {
        return (
            <React.Fragment>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P1
                        <button type="button" onClick={handleOpen}>
                            Open Modal
                        </button>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {body}
                        </Modal>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P2
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                                Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P3
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                                Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P4
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P5
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P6
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P7
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P8
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
            </React.Fragment>
        );
    }

    //bottoni aule + numero di righe
    return (
        <div className={classes.root}>
            <ButtonGroup color="primary" aria-label="outlined primary button group">
                <Button>Aula 1</Button>
                <Button>Aula 2</Button>
                <Button>Aula 3</Button>
                <Button>Aula 4</Button>
            </ButtonGroup>
            <Grid container spacing={1}>
                <Grid container item xs={8} spacing={4}>
                    <FormRow />
                </Grid>
            </Grid>
        </div>
    );
}
