import React from 'react';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            flexGrow: 1,
            display: 'flex',
            flexDirection: 'column',
            //alignItems: 'center',
            '& > *': {
                margin: theme.spacing(1),
            }
        },
        paper: {
            textAlign: 'center',
            color: theme.palette.text.secondary,
            position: 'relative',
            width: 100,
            backgroundColor: theme.palette.background.paper,
            border: '2px solid #000',
            boxShadow: theme.shadows[5],
            padding: theme.spacing(2, 4, 3),
        },
    }),
);

export default function DisabledTabs() {
    const [value, setValue] = React.useState(2);

    const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
        setValue(newValue);
    };
};

{/*contenuto del modale*/ }
export default function SimpleModal() {
    const classes = useStyles();
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

    {/*handleOpen apre popup*/ }
    const handleOpen = () => {
        setOpen(true);
    };

    {/*handleClose chiude popup*/}
    const handleClose = () => {
        setOpen(false);
    };

    const body = (
        <div style={modalStyle} className={classes.paper}>
            <button type="button" onClick={handleClose}>
                Chiudi
            </button>
            <h2 id="simple-modal-title">Text in a modal</h2>
            <p id="simple-modal-description">
                Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
            </p>
            
        </div>
    );

    //numero di colonne
    function FormRow() {
        return (
            <React.Fragment>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>
                        <p>P1</p>
                        <button type="button" onClick={handleOpen}>
                            Gestisci
                        </button>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {body}
                            </Modal>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>
                        <p>P2</p>
                        <button type="button" onClick={handleOpen}>
                            Gestisci
                        </button>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            {body}
                        </Modal>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P3
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                                Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P4
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P5
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P6
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P7
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={classes.paper}>P8
                        <Typography variant="body2" style={{ cursor: 'pointer' }}>
                            Prenota
                        </Typography>
                    </Paper>
                </Grid>
            </React.Fragment>
        );
    }

    //bottoni aule + numero di righe
    return (
        <div className={classes.root}>
            <Paper square>
                <Tabs
                    value={value}
                    indicatorColor="primary"
                    textColor="primary"
                    onChange={handleChange}
                    aria-label="disabled tabs example"
                >
                    <Tab label="Active" />
                    <Tab label="Disabled" disabled />
                    <Tab label="Active" />
                </Tabs>
            </Paper>
            <Grid container spacing={1}>
                <Grid container item xs={10} spacing={2}>
                    <FormRow />
                </Grid>
            </Grid>
        </div>
    );
}
